import{_ as t,p as d,q as r,a1 as e}from"./framework-7db056f4.js";const a={},h=e('<h1 id="函数" tabindex="-1"><a class="header-anchor" href="#函数" aria-hidden="true">#</a> 函数</h1><h2 id="字符串函数" tabindex="-1"><a class="header-anchor" href="#字符串函数" aria-hidden="true">#</a> 字符串函数</h2><table><thead><tr><th>函数</th><th>功能</th></tr></thead><tbody><tr><td>concat(s1, s2,..., s3)</td><td>字符串拼接</td></tr><tr><td>lower(str)</td><td>将字符串str全部转为小写</td></tr><tr><td>upper(str)</td><td>将字符串str全部转换为大写</td></tr><tr><td>lpad(str, n, pad)</td><td>左填充，用字符串pad对str的左边进行填充，达到n个字符串长度</td></tr><tr><td>rpad(str, n, pad)</td><td>右填充，用字符串pad对str右边进行填充，达到n个字符串长度</td></tr><tr><td>trim(str)</td><td>去掉字符串头部和尾部的空格</td></tr><tr><td>substring(str, start, len)</td><td>返回从字符串str从start位置引起的len个长度的字符串</td></tr></tbody></table><h2 id="数值函数" tabindex="-1"><a class="header-anchor" href="#数值函数" aria-hidden="true">#</a> 数值函数</h2><table><thead><tr><th>函数</th><th>功能</th></tr></thead><tbody><tr><td>ceil()</td><td>向上取整</td></tr><tr><td>floor()</td><td>向下取整</td></tr><tr><td>mod(x, y)</td><td>返回x/y的模</td></tr><tr><td>rand()</td><td>返回0～1内的随机数</td></tr><tr><td>round(x, y)</td><td>求参数x的四舍五入的值，保留ｙ位小数</td></tr></tbody></table><h2 id="日期函数" tabindex="-1"><a class="header-anchor" href="#日期函数" aria-hidden="true">#</a> 日期函数</h2><table><thead><tr><th>函数</th><th>功能</th></tr></thead><tbody><tr><td>curdate()</td><td>返回当前日期</td></tr><tr><td>curtime()</td><td>返回当前时间</td></tr><tr><td>now()</td><td>返回当前日期和时间</td></tr><tr><td>year()</td><td>获取指date的年份</td></tr><tr><td>month()</td><td>获取指定date的月份</td></tr><tr><td>day()</td><td>获取指定date的日期</td></tr><tr><td>date_add(date, interval expr type)</td><td>返回一个日期/时间值加上一个时间间隔expr后的时间值</td></tr><tr><td>datediff(date1, date2)</td><td>返回起始时间date1 和 结束时间date2之间的天数</td></tr></tbody></table><h2 id="流程函数" tabindex="-1"><a class="header-anchor" href="#流程函数" aria-hidden="true">#</a> 流程函数</h2><table><thead><tr><th>函数</th><th>功能</th></tr></thead><tbody><tr><td>if(value, t, f)</td><td>如果value为true, 则返回t, 否则返回f</td></tr><tr><td>ifnull(value1, value2)</td><td>如果value1不为空, 返回value1, 否则返回value2</td></tr><tr><td>case when [val1] then [res1] ... else [default] end</td><td>如果val1为true，返回res1, ... 否则返回default默认值</td></tr><tr><td>case [expr] when [val1] then [res1] ... else [default] end</td><td>如果expr的值等于val1, 返回res1, ... 否则返回default默认值</td></tr></tbody></table>',9),s=[h];function l(n,i){return d(),r("div",null,s)}const c=t(a,[["render",l],["__file","06-函数.html.vue"]]);export{c as default};
